from backend.high_level.gestione_interna.enum.tipo_opera import TipoOpera


class Composizione:
    def __init__(self, altezzaCm: float=0, larghezzaCm: float=0,
                 profonditaCm: float=0, pesoGr: float=0, tipo: TipoOpera=TipoOpera.TELA):
        self.tipo_opera = tipo
        self.altezza_cm = altezzaCm
        self.larghezza_cm = larghezzaCm
        self.profondita_cm = profonditaCm
        self.peso_gr = pesoGr

    def calcolaArea(self) -> float:
        """
        Metodo per il calcolo dell'area dell'opera.\n
        :return: il valore <b2>float</b2> dell'area.
        """
        return self.altezza_cm * self.larghezza_cm

    def calcolaVolume(self) -> float:
        """
        Metodo per il calcolo del volume dell'opera.\n
        :return: il valore <b2>float</b2> del volume.
        """
        return self.calcolaArea() * self.profondita_cm


